#pragma once#include <array>#include <vector>#include <Eigen/Dense>#include <GraphMol/Conformer.h>#include "PreComputedData.h"using MatX3d = Eigen::Matrix<double, Eigen::Dynamic, 3, Eigen::RowMajor>;struct ECHOWeights {       double aromatic = 0.0;    double nonbond = 0.0;    double saltbridge_raw = 0.0;    double hbond_raw = 0.0;    double ligand_intra = 0.0;    double ligand_torsion = 0.0;    double electro_attractive = 0.0;    double electro_repulsive_clamp = 0.0;    double desolvation_penalty_scaled = 0.0;    double hphobe_raw_hpho = 0.0;    double hphobe_raw_hpil = 0.0;    double hphob_enc_gt_7_only_hpho = 0.0;    double hphob_enc_gt_7_only_hpil_unsat = 0.0;    double unsat_polar = 0.0;        static ECHOWeights default_v1() {        ECHOWeights w;        w.hphobe_raw_hpil               = -5.02064699295;        w.hphobe_raw_hpho               = -3.82151072767;        w.hphob_enc_gt_7_only_hpil_unsat =  0.135653137605;        w.hphob_enc_gt_7_only_hpho      =  0.980965526852;        w.desolvation_penalty_scaled    = -0.549608417911;        w.electro_repulsive_clamp       = -0.174470829679;        w.electro_attractive            = -0.167175136609;        w.saltbridge_raw                = -0.144841339007;        w.unsat_polar                   = -0.077271588204;        w.ligand_torsion                = -0.922922299083;        w.ligand_intra                  = -0.00227529578223;        w.nonbond                       = -0.0380536462386;        w.hbond_raw                     = -0.0287929978597;        w.aromatic                      = -0.0151983247593;        return w;    }};struct ECHOScore {    const PreComputedData& pre;    const ECHOWeights weights;    const std::vector<Eigen::RowVector3d>* waters = nullptr;    const std::vector<std::array<Eigen::RowVector3d, 2>>* water_Hs = nullptr;    double interaction_cutoff = 6.0;    double electro_clamp = 2.0;    explicit ECHOScore(const PreComputedData& pre_,                       ECHOWeights w = ECHOWeights::default_v1())        : pre(pre_), weights(w) {}    double score(const RDKit::Conformer& conf, double rep_max = 5.0) const;};